(127, 'Vị trí KDC Areca Villa Long An', 'vi-tri-kdc-areca-villa-long-an', 'Vi tri KDC Areca Villa Long An', NULL, NULL, 999, '<div style=\"text-align:justify\"><strong>KDC Areca Villa Long An&nbsp;c&oacute; vị tr&iacute; thuận lợi như:</strong><br />\r\n<br />\r\n&ndash; C&aacute;ch trung t&acirc;m Th&agrave;nh phố Hồ Ch&iacute; Minh 22km.<br />\r\n<br />\r\n&ndash; C&aacute;ch trung t&acirc;m chợ Đức H&ograve;a 700m.<br />\r\n<br />\r\n&ndash; C&aacute;ch khu c&ocirc;ng nghiệp T&acirc;n Đức, T&acirc;n Đ&ocirc; 2km.<br />\r\n<br />\r\n&ndash; C&aacute;ch trường tiểu học, trung học, trung học phổ th&ocirc;ng 1,5km.<br />\r\n<br />\r\n&ndash; Thuận tiện c&aacute;c hướng giao th&ocirc;ng đi về T&acirc;y Ninh, H&oacute;c M&ocirc;n, TT. Bến Lức.<br />\r\n&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/ket-noi-vung-areca-VILLA.png\" /></div>\r\n', 0, 6, 2, 0, 0, 501, '2018-03-22 17:44:44', '2018-03-22 17:44:44', 1, 1),
(128, 'Tiện ích Areca Villa Long An', 'tien-ich-areca-villa-long-an', 'Tien ich Areca Villa Long An', NULL, NULL, 999, '<strong>Areca Villa Long An</strong>&nbsp;nằm tại một vị tr&iacute; v&ocirc; c&ugrave;ng đắc địa n&ecirc;n cư d&acirc;n sinh sống tại đ&acirc;y cũng sẽ được thừa hưởng tất cả c&aacute;c tiện &iacute;ch ph&ugrave; hợp với đẳng cấp m&agrave; dự &aacute;n mang lại như: chợ, trường học, bệnh viện, khu vui chơi giải tr&iacute;, c&ocirc;ng vi&ecirc;n, trung t&acirc;m thương mại,&hellip;. V&agrave; c&ograve;n nhiều tiện &iacute;ch ngoại khu hấp dẫn kh&aacute;c đang chờ bạn kh&aacute;m ph&aacute;.<br />\r\n<br />\r\n<img alt=\"\" src=\"/uploads/images/tien%20ich%283%29.jpg\" />', 0, 6, 3, 0, 0, 502, '2018-03-22 20:07:04', '2018-03-22 20:07:04', 1, 1),
(129, 'Thiết kế Long An Areca Villa', 'thiet-ke-long-an-areca-villa', 'Thiet ke Long An Areca Villa', NULL, NULL, 999, 'Được khởi nguồn &yacute; tưởng từ vi&ecirc;n ngọc ch&acirc;u mang đậm n&eacute;t ki&ecirc;u sa, qu&yacute; ph&aacute;i. <strong>Đất nền Long An Areca Villa</strong> như một điểm tụ hội những gi&aacute; trị tinh t&uacute;y của đất trời, thu h&uacute;t kh&iacute; t&agrave;i, lộc vượng. Tại đ&acirc;y, mỗi căn biệt thự nh&agrave; phố ch&iacute;nh l&agrave; một vi&ecirc;n ngọc nhỏ, được &ocirc;m trọn trong chiếc vỏ ngọc trai. T&ocirc;n tạo n&ecirc;n vẻ đẹp danh gi&aacute;, đẳng cấp sang trọng, nhưng vẫn mang phong c&aacute;ch ri&ecirc;ng của gia chủ.\r\n<div style=\"text-align:center\"><img src=\"http://thanhphuthinhland.vn/uploads//post/2018/03/22/thiet-ke-1521724492.jpg\" style=\"max-width:100%\" /><br />\r\n<img src=\"http://thanhphuthinhland.vn/uploads//post/2018/03/22/thiet-ke-areca-villa-long-an-1521724492.jpg\" style=\"max-width:100%\" /><br />\r\n<img src=\"http://thanhphuthinhland.vn/uploads//post/2018/03/22/thiet-ke-arica-villa-long-an-1521724492.jpg\" style=\"max-width:100%\" /></div>\r\n', 0, 6, 4, 0, 0, 503, '2018-03-22 20:15:47', '2018-03-22 20:15:47', 1, 1),
(130, 'PTTT Areca Villa Long An', 'pttt-areca-villa-long-an', 'PTTT Areca Villa Long An', NULL, NULL, 999, '<strong>Khu d&acirc;n cư Areca Villa</strong> c&oacute; 2 phương thức thanh to&aacute;n hỗ trợ Qu&yacute; kh&aacute;ch h&agrave;ng :<br />\r\n<br />\r\n<strong>Phương thức 1</strong>:<br />\r\n<br />\r\n&rarr; Dự kiến ng&agrave;y 18 hoặc ng&agrave;y 23/03/2018 mở b&aacute;n (Kh&aacute;ch h&agrave;ng chuyển cọc)<br />\r\n<br />\r\n&rarr; 7 ng&agrave;y sau thanh to&aacute;n 50% k&yacute; HĐMB<br />\r\n<br />\r\n&rarr; 1 th&aacute;ng sau thanh to&aacute;n 50% c&ocirc;ng chứng sang t&ecirc;n sổ đỏ.<br />\r\n<br />\r\n<strong>Phương Thức 2:</strong><br />\r\n<br />\r\n&rarr; Dự kiến ng&agrave;y 18 hoặc ng&agrave;y 23/03/2018 mở b&aacute;n (Kh&aacute;ch h&agrave;ng chuyển cọc)<br />\r\n<br />\r\n&rarr; 7 ng&agrave;y sau thanh to&aacute;n 100% c&ocirc;ng chứng sang t&ecirc;n sổ đỏ.', 0, 6, 5, 0, 0, 504, '2018-03-22 20:19:38', '2018-03-22 20:19:38', 1, 1),
(131, 'Đất nền Đức Hòa Long An bứt phá sau một thời gian trầm lắng', 'dat-nen-duc-hoa-long-an-but-pha-sau-mot-thoi-gian-tram-lang', 'Dat nen Duc Hoa Long An but pha sau mot thoi gian tram lang', 'Với vị trí tiếp giáp TP. HCM, cộng thêm chính sách quy hoạch liên kết vùng , bất động sản Long An đang trở thành thị trường mới nổi đầy hấp dẫn với các nhà đầu tư. Nổi bật nhất là thị trường đất nền huyện Đức Hòa.', '2018/03/22/dat-nen-long-an-dat-nen-duc-hoa-1521726479.jpg', 7, '<p>Sau một thời gian trầm lắng, nhường sự n&oacute;ng sốt cho thị trường bất động sản ph&iacute;a Đ&ocirc;ng S&agrave;i G&ograve;n. Tới thời điểm hiện tại, đất nền khu T&acirc;y đ&atilde; lấy lại vị thế khi m&agrave; c&aacute;c nh&agrave; đầu tư từ lớn cho tới thứ cấp đ&atilde; đổ về đ&acirc;y.<br />\r\n<br />\r\n<strong>Thị trường nhiều lợi thế</strong></p>\r\n\r\n<p>Với vị tr&iacute; địa l&yacute; đắc địa, ph&iacute;a Bắc gi&aacute;p huyện Trảng B&agrave;ng, tỉnh T&acirc;y Ninh, huyện Củ Chi, huyện H&oacute;c M&ocirc;n Tp. HCM; ph&iacute;a Đ&ocirc;ng Nam gi&aacute;p huyện B&igrave;nh Ch&aacute;nh, Tp. HCM. Thuộc trung t&acirc;m khu đ&ocirc; thị T&acirc;y Bắc Th&agrave;nh phố Hồ Ch&iacute; Minh. Từ Đức H&ograve;a cư d&acirc;n c&oacute; thể di chuyển v&agrave; li&ecirc;n kết thuận lợi với c&aacute;c quận thuộc trung t&acirc;m Th&agrave;nh phố Hồ Ch&iacute; Minh chỉ với 25 ph&uacute;t đi xe c&ugrave;ng hệ thống giao th&ocirc;ng v&ocirc; c&ugrave;ng thuận lợi th&ocirc;ng qua Quốc lộ 22 v&agrave; Quốc lộ 1.</p>\r\n\r\n<p>Ngo&agrave;i ra, huyện Đức H&ograve;a c&ograve;n sở hữu 18 khu c&ocirc;ng nghiệp (KCN) v&agrave; cụm CN dẫn đầu khu vực về thu h&uacute;t vốn FDI. Vừa qua Đức H&ograve;a đ&atilde; được th&ocirc;ng qua nghị quyết ph&ecirc; duyệt c&ocirc;ng nhận l&agrave; khu đ&ocirc; thị loại 4, hứa hẹn trở th&agrave;nh &ldquo;Th&agrave;nh phố mới của Long An&rdquo; trong tương lai, giống như sự ph&aacute;t triển mạnh mẽ của B&igrave;nh Dương ng&agrave;y trước. Hiện Đức H&ograve;a đ&atilde; vươn m&igrave;nh trở th&agrave;nh thị trường &ldquo;chiến lược&rdquo; đầy tiềm năng cho c&aacute;c nh&agrave; đầu tư bất động sản.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/dat%20nen%20long%20an%20dat%20nen%20duc%20hoa.jpg\" /></p>\r\n\r\n<p><strong>Kh&ocirc;ng lo &ldquo;sốt ảo&rdquo;</strong></p>\r\n\r\n<p>Nhận diện được tiềm năng v&ocirc; c&ugrave;ng to lớn của Đức H&ograve;a, thời gian qua kh&aacute; nhiều nh&agrave; đầu tư t&ecirc;n tuổi đ&atilde; đổ vốn đầu tư v&agrave;o khu vực n&agrave;y. C&aacute;c dự &aacute;n nổi bật tại Đức H&ograve;a c&oacute; thể kể đến như: dự &aacute;n L&agrave;ng Sen do C&ocirc;ng ty Ph&uacute;c Khang l&agrave;m chủ đầu tư; dự &aacute;n Khu d&acirc;n cư C&aacute;t Tường Ph&uacute; Thạnh, C&aacute;t Tường Ph&uacute; Nam do CTCP Địa ốc C&aacute;t Tường Đức H&ograve;a đầu tư, dự &aacute;n C&aacute;t Tường Ph&uacute; Nguy&ecirc;n Residence, C&aacute;t Tường Ph&uacute; Sinh&hellip;<br />\r\n<br />\r\nNếu đầu tư d&agrave;i hạn th&igrave; c&aacute;c dự &aacute;n lớn sẽ cho gi&aacute; trị tốt hơn. V&igrave; c&oacute; thể sau 7-10 năm sau c&aacute;c tiện &iacute;ch nội khu được h&igrave;nh th&agrave;nh th&igrave; đất đai b&ecirc;n trong sẽ c&oacute; 1 nấc thanh mới. Tuy nhi&ecirc;n nếu đầu tư ngắn hạn hay c&ograve;n gọi l&agrave; &quot;lướt s&oacute;ng&quot; th&igrave; c&aacute;c dự &aacute;n vừa v&agrave; nhỏ sẽ c&oacute; gi&aacute; trị tăng trưởng tốt hơn. L&yacute; giải điều n&agrave;y đ&oacute; ch&iacute;nh l&agrave; việc ra sổ, 1 dự &aacute;n nhỏ th&igrave; việc ra sổ sẽ nhanh hơn, kh&ocirc;ng bị treo l&acirc;u v&igrave; mọi quy tr&igrave;nh đều được r&uacute;t gọn. Thứ 2 l&agrave; lợi nhuận cao&nbsp;hơn, thời gian lướt r&uacute;t ngắn hơn&nbsp;v&igrave; dự &aacute;n b&aacute;n ra v&agrave; b&aacute;n nhanh ở đợt 1, thường th&igrave; đợt 3 l&agrave; hết. L&uacute;c n&agrave;y c&aacute;c nh&agrave; đầu tư thứ cấp mua ở đợt 1 c&oacute; thể b&aacute;n lại cho thị trường F2&nbsp;với lợi nhuận kỳ vọng cao hơn nhiều so với đợt mở b&aacute;n đầu ti&ecirc;n.<br />\r\n<br />\r\nVới sự gia tăng mạnh của c&ocirc;ng nh&acirc;n khu vực Đức H&ograve;a v&agrave; ch&iacute;nh s&aacute;ch giảm tải &aacute;p lực d&acirc;n số từ Hồ Ch&iacute; Minh th&igrave; c&aacute;c v&ugrave;ng kế cận với HCM sẽ trở th&agrave;nh lựa chọn an cư l&yacute; tưởng. Ch&iacute;nh v&igrave; thế t&iacute;nh thanh khoản thị trường bất động sản c&aacute;c khu vực n&agrave;y sẽ tăng l&ecirc;n từng ng&agrave;y. Ch&iacute;nh điều n&agrave;y sẽ tr&aacute;nh cơn c&aacute;c sốt ảo c&oacute; thể xảy ra khi cung vượt cầu.<br />\r\n<br />\r\nNếu được&nbsp;chọn để đầu tư ngắn hạn th&igrave; dự &aacute;n&nbsp;<strong>Areca Villa Long An</strong>&nbsp;l&agrave; một lựa chọn tốt tại khu vực Long An n&oacute;i chung v&agrave; Đức H&ograve;a n&oacute;i ri&ecirc;ng. Đ&acirc;y l&agrave; dự &aacute;n c&oacute; quy m&ocirc; vừa với 7,5ha, gi&aacute; tốt v&agrave; c&oacute; ng&acirc;n h&agrave;ng Sacombank hỗ trợ tới 70%.&nbsp;Mọi chi tiết về dự &aacute;n đầy tiềm năng n&agrave;y qu&yacute; vị vui l&ograve;ng truy cập đường dẫn:&nbsp;<a href=\"http://thanhphuthinhland.vn/du-an/du-an-kdc-areca-villa\">http://thanhphuthinhland.vn/du-an/du-an-kdc-areca-villa</a></p>\r\n', 1, NULL, NULL, 1, 0, 505, '2018-03-22 20:47:45', '2018-03-22 20:58:25', 1, 1),
(132, 'Dự Án KDC Areca Villa Long An - sự bứt phá ngoạn mục', 'du-an-kdc-areca-villa-cuoc-but-pha-ngoan-muc', 'Du An KDC Areca Villa Long An - su but pha ngoan muc', 'Cách chợ Đức Hòa chỉ 500m,  Cách mặt lộ 824 chỉ 800m. Chỉ trong vòng 2 tuần bán hết sạch toàn bộ 200 nền của giai đoạn 1.  Đó là những con số không phải dự án có quy mô tầm trung nào ở Long An có thể làm được.', '2018/04/15/4635-5aad31f4c3932-1523760597.jpg', 7, 'Bắt đầu từ nửa đầu năm 2017, sau ch&iacute;nh s&aacute;ch của UB th&agrave;nh phố về ph&acirc;n l&ocirc; b&aacute;n nền khu vực HCM v&agrave; những kh&oacute; khăn về ph&aacute;p l&yacute; tại khu vực s&acirc;n bay Long Th&agrave;nh m&agrave; đất Long An c&oacute; cơ hội tăng tốc, bứt ph&aacute; ngoạn mục về mật độ x&acirc;y dựng dự &aacute;n v&agrave; số lượng nh&agrave; đầu tư thứ cấp đổ về đ&acirc;y. Hầu hết c&aacute;c dự &aacute;n từ nhỏ đến lớn ở đều thanh khoản tốt. Tuy nhi&ecirc;n để b&aacute;n hết s&aacute;ch trong v&ograve;ng 2 tuần th&igrave; kh&ocirc;ng phải dự &aacute;n n&agrave;o cũng l&agrave;m được. V&agrave; c&oacute; 1 dự &aacute;n đ&atilde; tạo n&ecirc;n sự bứt ph&aacute;t ngoạn mục đ&oacute; ch&iacute;nh l&agrave;&nbsp;KDC Areca Villa Long An.<br />\r\n<br />\r\nNhờ sở hữu vị tr&iacute; đẹp, mặt bằng vu&ocirc;ng vức, c&aacute;c cung đường chạy v&agrave;o dự &aacute;n rất thuận lợi m&agrave; c&aacute;c vị kh&aacute;c t&igrave;m tới đ&acirc;y kh&ocirc;ng hề kh&oacute;. Khi bung ra thị trường với mức gi&aacute; giao động từ 8 -12 triệu khiến nhiều sale quan ngại về thời gian c&oacute; thể b&aacute;n hết được cả dự &aacute;n. Tuy nhi&ecirc;n so với c&aacute;c dự &aacute;n kh&aacute;c trong khu vực th&igrave; mức gi&aacute; n&agrave;y l&agrave; hợp l&yacute;. Về bản chất th&igrave;&nbsp;KDC Areca Villa Long An chỉ l&agrave; ph&acirc;n l&ocirc; b&aacute;n nền n&ecirc;n kh&ocirc;ng thể so s&aacute;nh tiện &iacute;ch nội khu so với c&aacute;c khu độ thị như Lexington, An Hạ, L&agrave;ng Sen, E.City ...Tuy nhi&ecirc;n&nbsp;KDC Areca Villa Long An lại mạnh về tiện &iacute;ch ngoại khu c&oacute; b&aacute;n k&iacute;nh c&oacute; khi c&ograve;n ngắn hơn so với tiện &iacute;ch nội khu của c&aacute;c dự &aacute;n lớn kh&aacute;c. Đ&oacute; ch&iacute;nh l&agrave; điện đường, trường trạm, c&aacute;i hay m&agrave; nhiều kh&aacute;ch h&agrave;ng lựa chọn&nbsp;KDC Areca Villa Long An đ&oacute; l&agrave; c&aacute;c tiện &iacute;ch n&agrave;y đ&atilde; tồn tại v&agrave; ph&aacute;t triển, c&ograve;n c&aacute;c tiện &iacute;ch của KĐT kh&aacute;c hầu hết chỉ nằm tr&ecirc;n bản vẽ, về t&iacute;nh thực thi c&ograve;n mơ hồ, trừu tượng.<br />\r\n&nbsp;\r\n<div style=\"text-align:center\"><img src=\"http://thanhphuthinhland.vn/uploads//post/2018/04/15/tien-ich-kdc-areca-villa-long-an-1523759732.jpg\" style=\"max-width:100%\" /></div>\r\n\r\n<div style=\"text-align:center\"><em>Tiện &iacute;ch ngoại khu của Areca Villa Long An</em></div>\r\n\r\n<div>Ch&iacute;nh v&igrave; c&aacute;c yếu tố tr&ecirc;n cộng với sự n&oacute;ng l&ecirc;n tr&ocirc;ng thấy của thị trường, c&aacute;c tiềm năng ph&aacute;t triển trong tương lai m&agrave;&nbsp;KDC Areca Villa Long An đ&atilde; được hầu hết c&aacute;c kh&aacute;ch h&agrave;ng khi gh&eacute; thăm đồng &yacute; giao dịch. Kh&ocirc;ng những thế m&agrave; chỉ c&oacute; 2 tuần th&ocirc;i, to&agrave;n bộ số nền của giai đoạn 1 đ&atilde; ch&aacute;y h&agrave;ng. Đ&acirc;y l&agrave; một kết quả ngo&agrave;i dự đo&aacute;n, v&igrave; thời gian bung h&agrave;ng l&agrave; thời gian vừa nghỉ tết, n&ecirc;n chủ đầu tư l&agrave;&nbsp;<strong>C&ocirc;ng ty bất động sản Long An Delta</strong>&nbsp;c&ugrave;ng c&aacute;c s&agrave;n ph&acirc;n phối như Salem, <strong>Thanh Ph&uacute; Thịnh Land</strong> ... dự định sẽ b&aacute;n hết sau&nbsp;nhất 4 tuần.</div>\r\n\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"\" src=\"/uploads/images/tien%20ich(3).jpg\" style=\"height:1950px; width:3000px\" /></div>\r\n\r\n<div><br />\r\nTừ sự kiện&nbsp;Dự &Aacute;n KDC Areca Villa Long An ch&aacute;y h&agrave;ng sau 2 tuần đ&atilde; dự b&aacute;o một năm m&agrave; bất động sản Long An sẽ bước sang 1 trang mới c&oacute; nhiều cơ hội ph&aacute;t triển rực rỡ. R&otilde; r&agrave;ng c&aacute;c huyện l&acirc;n cận với HCM như Đức H&ograve;a, Cần Đước, Cần Giuộc th&igrave; tiềm năng ph&aacute;t triển c&ograve;n d&agrave;i v&igrave; sự chuyển dịch d&acirc;n số, cơ ở hạ tầng để giảm &aacute;p lực từ th&agrave;nh phố cũng như ch&iacute;nh s&aacute;ch bỏ hộ khẩu của nh&agrave; nước ta. Thế n&ecirc;n qu&yacute; vị đang c&oacute; tiền nh&agrave;n rỗi hay đang đầu tư ở c&aacute;c khu vực đ&atilde; b&atilde;o h&ograve;a th&igrave; h&atilde;y t&igrave;m hiểu về bất động sản Long An, một mảnh đất được v&iacute; như con rồng nhỏ tại miền Nam.<br />\r\n<br />\r\nDo giai đoạn 1 đ&atilde; ch&aacute;y h&agrave;ng n&ecirc;n nếu qu&yacute; kh&aacute;ch quan t&acirc;m th&igrave; h&atilde;y chờ giai đoạn 2 sẽ được mở b&aacute;n v&agrave;o m&ugrave;a h&egrave; năm nay. Li&ecirc;n hệ đặt chỗ trước theo hotline <span style=\"color:#ff0000\"><strong>0919.356.178</strong></span> hoặc t&igrave;m hiểu th&ecirc;m về dự &aacute;n theo đường dẫn:&nbsp;<a href=\"http://thanhphuthinhland.vn/du-an/du-an-kdc-areca-villa\" target=\"_blank\">http://thanhphuthinhland.vn/du-an/du-an-kdc-areca-villa</a></div>\r\n\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n', 1, NULL, NULL, 0, 0, 506, '2018-04-15 09:49:44', '2018-05-08 16:45:24', 1, 1),
(133, 'Eco Garden 3 - bán tiện ích sống thật, không hứa hẹn tương lai', 'eco-garden-3-ban-tien-ich-song-that-khong-hua-hen-tuong-lai', 'Eco Garden 3 - ban tien ich song that, khong hua hen tuong lai', 'Với quy mô chỉ 8000 m2 nằm trên mặt tiền tỉnh lộ 826, cách ngã tư Xoài Đôi 500m và mất chỉ 7 phút để về HCM.  Với nhiều tiện ích hiện hữu bao quanh dự án thì cái tên Eco Garden 3 là một lựa chọn để sống tốt chứ không chỉ đầu tư.', '2018/04/15/du-an-eco-garden-3-1523764685.png', 7, 'Nếu qu&yacute; vị l&agrave; tu&yacute;p người th&iacute;ch cuộc sống kh&ocirc;ng qu&aacute; ồn &agrave;o nhưng phải đầy đủ tiện &iacute;ch, th&iacute;ch sống trong một khu nội bộ c&oacute; quy m&ocirc; vừa phải với c&aacute;c căn biệt thự, nh&agrave; phố khang trang s&aacute;t kề nhau. Một nơi m&agrave; c&aacute;c vấn đề an ninh, tiện nghi lu&ocirc;n đảm bảo th&igrave;&nbsp;<strong>Eco Garden 3</strong> ch&iacute;nh l&agrave; một lựa chọn tốt.<br />\r\n<br />\r\nC&aacute;ch đ&acirc;y 2 th&aacute;ng tuyến đường từ Đinh Đức Thiện (B&igrave;nh Ch&aacute;nh) nối d&agrave;i với 826 c&ograve;n một số đoạn l&ocirc; nh&ocirc;, &ugrave;n tắc th&igrave; b&acirc;y giờ mọi thứ kh&aacute;c, mặt đường bằng phẳng, l&ograve;ng đường được nới ra, một số chợ d&acirc;n sinh bị giải tỏa để đảm bảo an to&agrave;n giao th&ocirc;ng. Điều n&agrave;y cho thấy ch&iacute;nh quyền nơi đ&acirc;y đ&atilde; c&oacute; sự đầu tư về cơ sở hạ tầng, gi&uacute;p cho người d&acirc;n sinh sống dọc theo tuyến đường n&agrave;y cũng như c&aacute;c khu vực phụ cận dễ d&agrave;ng di chuyển l&ecirc;n th&agrave;nh phố m&agrave; kh&ocirc;ng gặp t&igrave;nh trạng kẹt xe, ổ g&agrave; ổ voi.<br />\r\n<br />\r\nNếu qu&yacute; vị đi từ ph&iacute;a chợ B&igrave;nh Ch&aacute;nh v&agrave;o Eco Garden 3 th&igrave; chỉ mất khoảng 10 ph&uacute;t để đến với dự &aacute;n.&nbsp;Eco Garden 3 Nằm tr&ecirc;n mặt tiền đường tỉnh lộ 40m 826. C&aacute;i hay l&agrave; n&oacute; kh&ocirc;ng nằm s&aacute;t mặt đường, m&agrave; nằm lọt v&agrave;o trong, đằng sau những rặng dừa. Điều n&agrave;y sẽ gi&uacute;p khu d&acirc;n cư tr&aacute;nh được kh&oacute;i bụi, c&aacute;i ồn &agrave;o của xe cộ.<br />\r\n<br />\r\nTrong v&ograve;ng b&aacute;n k&iacute;nh 2m th&igrave; dường như mọi thứ đều c&oacute; từ điện đường trường trạm, chợ b&uacute;a, ch&ugrave;a chiền ...&nbsp;Eco Garden 3 l&agrave; để sống, để an cư chứ kh&ocirc;ng chỉ mua rồi để đ&oacute; chờ tăng gi&aacute;. Người ta n&oacute;i &quot;nhỏ m&agrave; c&oacute; v&otilde;&quot; ch&iacute;nh l&agrave; đ&acirc;y. Vị tr&iacute; đẹp, gi&aacute; tốt, tiện &iacute;ch đầy đủ, hạ tầng đạt chuẩn với đường nội bộ trải nhựa rộng 6 m&eacute;t, c&oacute; c&acirc;y xanh, cống tho&aacute;t nước, điện nước chuẩn quốc gia. S&aacute;t b&ecirc;n với khu d&acirc;n cư hiện hữu, tiện &iacute;ch cuộc sống gần như ho&agrave;n thiện thế n&ecirc;n nếu qu&yacute; vị mua ở th&igrave; c&oacute; thể x&acirc;y ở ngay m&agrave; kh&ocirc;ng phải lo lắng về bất cứ điều g&igrave;.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>Cập nhật mới nhất:</strong></span><br />\r\n&nbsp;\r\n<div style=\"text-align:center\"><span style=\"color:#ff0000\"><iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/LRzFuTqjZEs?rel=0&amp;showinfo=0\" frameborder=\"0\" allow=\"autoplay; encrypted-media\" allowfullscreen></iframe></span></div>\r\n<br />\r\nTừ việc ph&acirc;n phối c&aacute;c dự &aacute;n lớn cho tới nhỏ, <strong>Thanh Ph&uacute; Thịnh Land</strong> nhận thấy, ở ph&acirc;n kh&uacute;c n&agrave;o cũng c&oacute; c&aacute;i hay của n&oacute;. Th&iacute; dụ với c&aacute;c dự &aacute;n lớn, chắc chắn quy m&ocirc; sẽ l&agrave;m cho c&aacute;c nh&agrave; đầu tư thứ cấp quan t&acirc;m. V&igrave; sau n&agrave;y họ đẩy h&agrave;ng ra cũng dễ. Tuy nhi&ecirc;n thực tế đ&ocirc;i khi kh&ocirc;ng phải khi n&agrave;o cũng như vậy. Dự &aacute;n lớn nếu qu&aacute; lớn rất dễ bị d&iacute;nh ph&aacute;p l&yacute; dẫn đến trễ tiến độ x&acirc;y dưng tiện &iacute;ch nội khu, tệ hơn l&agrave; vấn đề sổ s&aacute;ch. Dự &aacute;n nhỏ hầu hết sẽ c&oacute; vị tr&iacute; đẹp hơn v&igrave; n&oacute; dễ mua, dễ l&agrave;m sổ. Ch&iacute;nh v&igrave; thế m&agrave; t&iacute;nh thanh khoản c&aacute;c dự &aacute;n nhỏ chắc chắn cao hơn. Dự &aacute;n lớn nếu l&agrave;m đ&uacute;ng cam kết, tiến độ th&igrave; quả nhi&ecirc;n lợi thế về sức b&aacute;n vẫn hơn dự &aacute;n nhỏ. Tuy nhi&ecirc;n đầu tư k&ecirc;nh n&agrave;o hiệu quả c&aacute;i n&agrave;y phần n&agrave;o cũng t&ugrave;y v&agrave;o chiến lược của người mua.<br />\r\n<br />\r\n<strong>Một điều đặc biệt</strong> l&agrave; khi mở b&aacute;n&nbsp;<strong>KDC Eco Garden 3</strong>&nbsp;ch&uacute;ng t&ocirc;i đ&atilde; quan niệm rằng <strong>chỉ&nbsp;b&aacute;n tiện &iacute;ch sống thật, kh&ocirc;ng hứa hẹn tương lai</strong>. Đơn giản l&agrave; moị thứ chuẩn bị cho một cuộc sống an cư đ&atilde; đầy đủ. Ch&uacute;ng t&ocirc;i kh&ocirc;ng cần phải hứa hẹn g&igrave; th&ecirc;m. R&otilde; r&agrave;ng gi&aacute; trị của n&oacute; được khẳng định chứ kh&ocirc;ng c&ograve;n mơ hồ trong 2 chữ &quot;tiềm năng&quot;.<br />\r\n<br />\r\nVới dự &aacute;n <strong>KDC Eco Garden 3</strong> v&agrave; hiện trạng c&aacute;c dự &aacute;n hiện tại th&igrave; r&otilde; r&agrave;ng qu&yacute; vị kh&ocirc;ng kh&oacute; để lựa chọn tại khu vực Cần Đước, Cần Giuộc. Mua ở hay đầu tư đều rất tốt. Khu vực n&agrave;y c&oacute; đ&agrave; tăng trưởng khiến giới đầu tư giật m&igrave;nh. Nếu như năm 2017 mức tăng trưởng đạt khoảng 60% th&igrave; năm 2018 dự b&aacute;o sẽ đạt th&ecirc;m 50 - 60 %. Kh&oacute; c&oacute; mảnh đất n&agrave;o đạt đ&agrave; tăng trưởng tốt như vậy. Những th&aacute;ng đầu năm 2018 biến động chưa nhiều n&ecirc;n h&atilde;y tranh thủ thời điểm v&agrave;ng n&agrave;y để đầu tư. C&oacute; một điều ch&uacute;ng t&ocirc;i <strong>chắc chắn l&agrave; gi&aacute; đất sẽ tăng cao</strong> v&agrave;o giữa năm 2018. V&agrave; cơ hội kiếm lời sẽ giảm dần theo thời gian. Đừng chần chừ nữa. H&atilde;y gọi v&agrave;o hotline <span style=\"font-size:16px\"><span style=\"color:#ff0000\"><strong>0919.356.178</strong></span></span> để c&oacute; được sự tư vấn tốt nhất về đầu tư cũng như th&ocirc;ng tin mới nhất về ch&iacute;nh s&aacute;ch chiết khấu, khung gi&aacute; ưu đ&atilde;i của&nbsp;<strong>KDC Eco Garden 3</strong>. C&aacute;m ơn qu&yacute; vị đ&atilde; quan t&acirc;m.', 1, NULL, NULL, 1, 0, 507, '2018-04-15 10:57:49', '2018-04-15 11:15:51', 1, 1),
(138, 'Đất nền vùng ven, kênh đầu tư mới của nhà đầu tư', 'dat-nen-vung-ven-kenh-dau-tu-moi-cua-nha-dau-tu', 'Dat nen vung ven, kenh dau tu moi cua nha dau tu', 'Với vị trí đẹp, cơ sở hạ tầng giao thông thuận lợi, rất nhiều dự án không ngừng được đầu tư, triển khai khiến cho \r\ncác khu vực ven TP.Hồ Chí Minh đặc biệt là Long An trở thành điểm đến cho nhiều nhà đầu tư đất nền trong nước và ngoài nuớc ồ ạc kéo về.', '2018/04/30/dau-tu-bat-dong-san-la-gi-1525045491.jpg', 7, '<h3>Nh&agrave; đầu tư bất động sản bắt đầu t&igrave;m kiếm k&ecirc;nh v&agrave; thị trường mới</h3>\r\nC&aacute;c nh&agrave; đầu tư cho rằng, quỹ đất tại thị trường TP.HCM đang trở n&ecirc;n cạn kiệt bởi nhiều quy định,&nbsp;nhiều c&ocirc;ng tr&igrave;nh đua nhau mọc l&ecirc;n trong khi c&aacute;c &nbsp;dự &aacute;n được ch&iacute;nh quyền địa phuơng ph&ecirc; duyệt th&igrave; lại&nbsp;kh&ocirc;ng thể triển khai v&igrave; nhiều l&yacute; do kh&aacute;c nhau, đ&atilde; tạo n&ecirc;n sức &eacute;p lớn cho thị truờng bất động sản nơi n&agrave;y.<br />\r\n<br />\r\nMặc d&ugrave; sau khi ch&iacute;nh quyền TP.HCM đ&atilde; sửa đổi quy đinh t&aacute;ch thửa đ&atilde; l&agrave;m cho đất nền&nbsp;tại một số khu vực trong th&agrave;nh phố đ&atilde; hạ nhiệt dữ ở mức ổn định. Nhưng gi&aacute; vẫn ở mức qu&aacute; cao, điều n&agrave;y đ&atilde; khiến nh&agrave; đầu tư e d&egrave; khi r&oacute;t vốn.&nbsp;<br />\r\n<br />\r\nKh&ocirc;ng chỉ đột biến về gi&aacute; cả tăng cao, m&agrave; vấn đề cơ sở hạ tầng giao th&ocirc;ng đang chiệu &aacute;p lực qu&aacute; tải do nhu cầu đi laịo qu&aacute; nhiều của nguời d&acirc;n.&nbsp;Thay v&igrave; đi l&agrave;m qu&atilde;ng đường 10 km v&agrave;o trung t&acirc;m TP.Hồ Ch&iacute; Minh mất hơn cả giờ đồng hồ,, trong khi từ v&ugrave;ng ven cụ thể l&agrave; từ Cần Giuộc Long An đến trung t&acirc;m TP.HCM&nbsp;<br />\r\nvới khoảng c&aacute;ch 50 - 60 km nhưng lại mất thời gian tương tự v&agrave; thậm ch&iacute; &iacute;t hơn.<br />\r\nNếu với 2 - 3 tỷ đồng, tại TP.HCM chỉ mua được đất, th&igrave; tại c&aacute;c tỉnh v&ugrave;ng ven, kh&ocirc;ng chỉ mua được đất m&agrave; c&ograve;n c&oacute; nh&agrave;, đồng thời c&ograve;n ph&aacute;t triển kinh doanh,&nbsp;l&atilde;i sinh l&atilde;i. Nh&agrave; đầu tư cần nh&igrave;n ra vấn đề để chuyển hướng cơ hội, t&igrave;m kiếm k&ecirc;nh đầu tư mới.<br />\r\n&nbsp;\r\n<h3 style=\"text-align:center\"><img src=\"http://thanhphuthinhland.vn/uploads//post/2018/04/30/dau-tu-bat-dong-san-long-an-1525045331.jpg\" style=\"max-width:100%\" /></h3>\r\n\r\n<h3><br />\r\nĐất nền V&ugrave;ng ven đặc biệt l&agrave; đất nền Long An c&oacute; cơ hội &quot;bay cao&quot;</h3>\r\nCơ hội bay cao l&agrave; bởi v&igrave; v&ugrave;ng ven như Cần Giộc Long An đang sở hữu nhiều yếu tố hấp dẫn mang t&iacute;nh quyết định đến lợi nhuận của nh&agrave; đầu tư.&nbsp;Điển h&igrave;nh như l&agrave; nh&agrave; đất Long An với lợi thế sở hữu vị tr&iacute; đắc địa khi nằm tr&ecirc;n tuyến&nbsp;quốc lộ giao thương kết nối TP.HCM - Khu Vực Miền T&acirc;y - B&igrave;nh Duơng - Đồng Nai v&agrave; rất gần với c&aacute;c khu c&ocirc;ng nghiệp hay đường cao tốc Bến Lức - Long Th&agrave;nh dự kiến th&ocirc;ng xe to&agrave;n tuyến v&agrave;o giữa năm 2018.<br />\r\n<br />\r\nĐất nền Long An gi&aacute; rẻ đang trở th&agrave;nh &ldquo;cơn sốt&rdquo;, với chi ph&iacute; v&agrave;i tỷ mới mua đuợc l&ocirc; đất tại TP.HCM, th&igrave; b&acirc;y giờ nh&agrave; đầu tư chỉ cần v&agrave;i trăm triệu đồng, ch&iacute; ph&iacute; bằng 1/3 chi ph&iacute; mua đất tại TP.HCM để mua một l&ocirc; đất nền tại Long An, cụ thể l&agrave; ở huyện Cần Giuộc.Theo c&aacute;c chuy&ecirc;n gia ph&acirc;n t&iacute;ch, thị truờng bất động sản tại long an c&ograve;n tăng truởng mạnh trong năm 2018.&nbsp;H&agrave;ng ng&agrave;n dự &aacute;n kh&ocirc;ng ngừng được thực hiện khiến cho khu vực Long An trở th&agrave;nh điểm đến cho nhiều chuy&ecirc;n gia đất nền trong nước v&agrave; quốc tế đầu tư.<br />\r\n<br />\r\nTrong số nhiều dự &aacute;n tại Long An hiện nay, chuỗi Dự &aacute;n Khu D&acirc;n Cư ECO GARDEN của c&ocirc;ng ty THANH PH&Uacute; THỊNH LAND&nbsp;đ&atilde; th&agrave;nh c&ocirc;ng rực rỡ đ&atilde; g&oacute;p phần tạo n&ecirc;n gi&aacute; trị cho đất nền khu vực Huyện Cần Giuộc Long An.&nbsp;Ch&iacute;nh những yếu tố: chi ph&iacute; đầu tư thấp, cơ hội lợi nhuận cao, gi&aacute; trị đảm bảo l&acirc;u d&agrave;i của c&aacute;c dự &aacute;n. Theo tiến tr&igrave;nh ph&aacute;t triển, đến năm 2030 Long An sẽ trở th&agrave;nh tỉnh c&ocirc;ng nghiệp theo hướng hiện đại. Ch&iacute;nh yếu tố n&agrave;y sẽ g&oacute;p phần tạo niềm tin&nbsp;đầu tư cho kh&aacute;ch h&agrave;ng, Biến đất nền Long An gia tăng lợi nhuận.', 0, NULL, NULL, 1, 0, 512, '2018-04-30 06:44:31', '2018-05-12 08:45:03', 20, 1),
(139, 'Những Quy định mới về luật đất đai năm 2018', 'nhung-quy-dinh-moi-ve-luat-dat-dai-nam-2018', 'Nhung Quy dinh moi ve luat dat dai nam 2018', 'Luật đất đai nói chung và luật đất đai xây dựng nói riêng là những thông tin cần thiết không thể thiếu đối với các nhà đầu tư bất động sản hiện nay. Để nắm đựoc thông tin mới nhất về luật đất đai hiện nay Thanh Phú Thịnh Land chúng tôi xin đưa ra một số quy định mới về luật đất đia năm 2018.', '2018/04/30/luat-dat-dai-1525047044.jpg', 5, '<h3>1. Bổ sung quy định nộp tiền thu&ecirc; đất khi chuyển mục đ&iacute;ch sử dụng đất</h3>\r\nĐối với trường hợp thu&ecirc; đất trả tiền thu&ecirc; đất h&agrave;ng năm th&igrave; chu kỳ ổn định đơn gi&aacute; thu&ecirc; đất t&iacute;nh từ thời điểm cơ&nbsp;quan nh&agrave; nước c&oacute; thẩm quyền ban h&agrave;nh quyết định gia hạn thời gian thu&ecirc; đất. Trường hợp thời điểm hết thời gian thu&ecirc; đất trước khi cơ quan nh&agrave; nước c&oacute; thẩm quyền ban h&agrave;nh quyết&nbsp;định gia hạn thời gian thu&ecirc; đất th&igrave; người sử dụng đất phải nộp tiền thu&ecirc; đất h&agrave;ng năm đối với&nbsp;khoảng thời gian n&agrave;y theo đơn gi&aacute; x&aacute;c định tr&ecirc;n cơ sở gi&aacute; đất tại Bảng gi&aacute; đất,&nbsp;hệ số điều chỉnh gi&aacute; đất, tỷ lệ phần trăm (%) t&iacute;nh đơn gi&aacute; thu&ecirc; đất do UBND cấp tỉnh ban h&agrave;nh.\r\n\r\n<h3>2. Sửa đổi, bổ sung một số quy định về việc xử l&yacute; tiền bồi thường, giải ph&oacute;ng mặt bằng</h3>\r\nBổ sung quy định về hồ sơ đề nghị khấu trừ tiền bồi thường, giải ph&oacute;ng mặt bằng, theo đ&oacute; hồ sơ bao gồm:<br />\r\nPhương &aacute;n bồi thường, giải ph&oacute;ng mặt bằng được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt: 01 bản sao<br />\r\nChứng từ v&agrave; bảng k&ecirc; thanh to&aacute;n tiền bồi thường, giải ph&oacute;ng mặt bằng: 01 bản ch&iacute;nh.&nbsp;Bổ sung th&ecirc;m Khoản 3a v&agrave;o Điều 13 về trường hợp Nh&agrave; nước thu hồi đất, thực hiện bồi thường,&nbsp;giải ph&oacute;ng mặt bằng v&agrave; cho thu&ecirc; đất theo h&igrave;nh thức trả tiền thu&ecirc; đất h&agrave;ng năm theo quy định của ph&aacute;p luật nhưng kh&ocirc;ng c&acirc;n&nbsp;đối được ng&acirc;n s&aacute;ch nh&agrave; nước để thực hiện bồi thường, giải ph&oacute;ng mặt bằng.\r\n<div style=\"text-align:center\"><img src=\"http://thanhphuthinhland.vn/uploads//post/2018/04/30/lat-dat-moii-1525047073.jpg\" style=\"max-width:100%\" /></div>\r\n\r\n<h3>3. Sửa đổi một số quy định về xử l&yacute; số tiền bồi thường, giải ph&oacute;ng mặt bằng</h3>\r\nBổ sung quy định về hồ sơ đề nghị khấu trừ tiền bồi thường, giải ph&oacute;ng mặt bằng, theo đ&oacute; hồ sơ gồm: Phương &aacute;n bồi thường, giải ph&oacute;ng mặt bằng được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt: 01 bản sao.<br />\r\n<br />\r\nChứng từ v&agrave; bảng k&ecirc; thanh to&aacute;n tiền bồi thường, giải ph&oacute;ng mặt bằng: 01 bản ch&iacute;nh. Trường hợp dự &aacute;n đầu tư được Nh&agrave; nước giao đất kh&ocirc;ng th&ocirc;ng qua h&igrave;nh thức đấu gi&aacute; c&oacute; h&igrave;nh thức sử dụng đất hỗn hợp:&nbsp;Giao đất c&oacute; thu tiền sử dụng đất, giao đất kh&ocirc;ng thu tiền sử dụng đất, cho thu&ecirc; đất v&agrave; nh&agrave; đầu tư tự nguyện ứng trước&nbsp;tiền bồi thường, giải ph&oacute;ng mặt bằng theo phương &aacute;n được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt th&igrave; việc khấu trừ tiền bồi thường,&nbsp;giải ph&oacute;ng mặt bằng được t&iacute;nh theo từng loại diện t&iacute;ch v&agrave; được ph&acirc;n bổ đều tiền bồi thường,&nbsp;giải ph&oacute;ng mặt bằng của phần diện t&iacute;ch đất sử dụng v&agrave;o mục đ&iacute;ch c&ocirc;ng cộng v&agrave;o c&aacute;c phần diện t&iacute;ch đất tương ứng.\r\n<h3>4. Sửa đổi, bổ sung một số quy định về việc thu tiền sử dụng đất khi nh&agrave; nước giao đất.</h3>\r\nĐơn gi&aacute; thu&ecirc; đất để x&aacute;c định số tiền thu&ecirc; đất theo quy định được x&aacute;c định dựa tr&ecirc;n cơ sở bảng gi&aacute; đất theo mục đ&iacute;ch sử dụng đất của dự &aacute;n.<br />\r\n- Hệ số điều chỉnh gi&aacute; đất t&iacute;nh thu tiền sử dụng đất, tỷ lệ phần trăm t&iacute;nh đơn gi&aacute; thu&ecirc; đất theo mục đ&iacute;ch sử dụng đất thương mại,&nbsp;dịch vụ do UBND cấp tỉnh ban h&agrave;nh.<br />\r\n- Quy định cụ thể tr&igrave;nh tự, thủ tục x&aacute;c định gi&aacute; khởi điểm đấu gi&aacute; quyền sử dụng đất để giao đất c&oacute; thu tiền sử dụng đất.\r\n<h3>5. Bổ sung quy định về gi&aacute; đất để t&iacute;nh tiền sử dụng đất.</h3>\r\nHệ số điều chỉnh gi&aacute; đất do UBND cấp tỉnh ban h&agrave;nh ng&agrave;y 01/01 h&agrave;ng năm.&nbsp;Thời điểm khảo s&aacute;t để x&acirc;y dựng hệ số điều chỉnh gi&aacute; đất được thực hiện từ ng&agrave;y 01/10 h&agrave;ng năm.<br />\r\n- C&aacute;c chi ph&iacute; li&ecirc;n quan đến việc x&acirc;y dựng hệ số điều chỉnh gi&aacute; đất (bao gồm cả chi ph&iacute; thu&ecirc; tổ chức tư vấn trong trường hợp cần thiết)&nbsp;được chi từ ng&acirc;n s&aacute;ch theo quy định của ph&aacute;p luật về ng&acirc;n s&aacute;ch nh&agrave; nước.<br />\r\n- Việc lựa chọn tổ chức tư vấn khảo s&aacute;t, x&acirc;y dựng hệ số điều chỉnh gi&aacute; đất được thực hiện theo quy định của ph&aacute;p luật về đấu thầu.<br />\r\n- Nếu tại thời điểm x&aacute;c định tiền sử dụng đất m&agrave; UBND cấp tỉnh chưa ban h&agrave;nh hệ số điều chỉnh gi&aacute; đất th&igrave;&nbsp;<br />\r\n&aacute;p dụng hệ số của năm trước đ&oacute; để x&aacute;c định tiền sử dụng đất phải nộp,&nbsp;UBND cấp tỉnh chịu tr&aacute;ch nhiệm về thất thu ng&acirc;n s&aacute;ch do chậm ban h&agrave;nh hệ số mới (nếu c&oacute;)<br />\r\n<br />\r\nVới c&aacute;c th&ocirc;ng tin , quy định mới của luật đất đai tr&ecirc;n ch&uacute;ng t&ocirc;i hy vọng gi&uacute;p cho qu&yacute; kh&aacute;ch c&oacute; những th&ocirc;ng tin bổ &iacute;ch nhất về luật đất đai hiện h&agrave;nh. L&agrave; nh&agrave; tư vấn, đầu tư bất động sản uy t&iacute;n ch&uacute;ng t&ocirc;i lu&ocirc;n lu&ocirc;n đặt vấn đề ph&aacute;p l&yacute; l&ecirc;n h&agrave;ng đầu v&agrave; tu&acirc;n thủ mọi quy định về luật đất đai của nh&agrave; nước.', 0, NULL, NULL, 0, 0, 513, '2018-04-30 07:12:33', '2018-05-11 15:58:44', 20, 1),
(140, 'Chọn hướng nhà theo tuổi nhâm tuất(1982)', 'chon-huong-nha-theo-tuoi-nham-tuat1982', 'Chon huong nha theo tuoi nham tuat(1982)', 'Theo phong thủy huớng nhà là ỵếu tố vô cùng quan trọng khi bạn xây dựng một căn nhà để an cư lạc nghiệp. \r\nHuớng nhà quyết định đến tài lộc, May mắn, sức khỏe...cho gia đình bạn. Sau đây chúng tôi xin đưa ra huớng xây nhà tốt nhất cho quý anh(NAM) sinh năm 1982 nhằm năm Giáp Tuất.', '2018/04/30/phongn-thuy-1525052470.jpg', 4, '<h3>1. Mệnh, ngũ h&agrave;nh, huớng nh&agrave; theo phong thủy</h3>\r\nNăm sinh dương lịch: 1982<br />\r\nNăm sinh &acirc;m lịch: Nh&acirc;m Tuất<br />\r\nQuẻ mệnh: Ly ( Hoả) thuộc Đ&ocirc;ng Tứ mệnh<br />\r\nNgũ h&agrave;nh: Nước giữa biển (Đại hải Thuỷ)<br />\r\nHướng nh&agrave; : hướng Nam (Hướng tốt), thuộc Đ&ocirc;ng Tứ trạch\r\n<div style=\"text-align:center\"><br />\r\n<img src=\"http://thanhphuthinhland.vn/uploads//post/2018/04/30/xem-huong-1525052556.png\" style=\"max-width:100%\" /><br />\r\n&nbsp;</div>\r\n\r\n<h3>2. V&ograve;ng b&aacute;t qu&aacute;i chi tiết huớng nh&agrave; theo phong thủy</h3>\r\nHướng tốt:&nbsp;<br />\r\nBắc - Di&ecirc;n ni&ecirc;n : Mọi sự ổn định .<br />\r\nĐ&ocirc;ng - Sinh kh&iacute; : Ph&uacute;c lộc vẹn to&agrave;n .<br />\r\nNam - Phục vị : Được sự gi&uacute;p đỡ .<br />\r\nĐ&ocirc;ng Nam - Thi&ecirc;n y : Gặp thi&ecirc;n thời được che chở .<br />\r\nHướng xấu:&nbsp;<br />\r\nT&acirc;y Bắc - Tuyệt mệnh : Chết ch&oacute;c .<br />\r\nĐ&ocirc;ng Bắc - Hoạ hại : Nh&agrave; c&oacute; hung kh&iacute; .<br />\r\nT&acirc;y - Ngũ qui : Gặp tai hoạ .<br />\r\nT&acirc;y Nam - Lục s&aacute;t : Nh&agrave; c&oacute; s&aacute;t kh&iacute; .<br />\r\n&nbsp;', 0, NULL, NULL, 0, 0, 514, '2018-04-30 08:44:14', '2018-05-11 15:55:35', 20, 1);
