(7, 'Tin thị trường', 'tin-thi-truong', 'Tin thi truong', '', '', '2017-05-05 14:54:21', '2017-07-14 09:30:00', 1, 1, 1, 0, 'Tin thị trường bất động sản khu vực TP.HCM và Long An', 'Tin thị trường bất động sản, tin về nhà đất khu vực tp.hcm và long an mới nhất, tin tức bds về các dự án và khu đô thị.', 'tin thị trường', ''),
(8, 'Tin Tức Long An', 'tin-tuc-long-an', 'Tin Tuc Long An', '', '', '2017-07-20 13:39:50', '2017-07-20 13:39:50', 0, 0, 1, 0, 'Tin tức tỉnh Long An mới nhất về các quy hoạch, văn bản hành chính', '', '', ''),
(9, 'Hỏi Đáp', 'hoi-dap', 'Hoi Dap', '', '', '2017-07-24 16:19:45', '2017-07-24 16:19:45', 0, 0, 1, 0, 'Chuyên mục hỏi đáp nhà đất, Hỏi đáp luật mua bán nhà ở', 'Chuyên mục hỏi đáp về nhà đất, hỏi đáp tư vấn pháp luật khi mua bán nhà. Hỏi đáp các thủ tục giấy tờ liên quan đến việc mua bán nhà đất, căn hộ, chung cư.', '', '');
